CREATE TABLE NOTICIAS (ID_NOTICIA BIGINT NOT NULL, AUTOR VARCHAR(255), FECHA_PUBLICACION DATE, TEXTO VARCHAR(255), TITULO VARCHAR(255), ONG_ID BIGINT, PRIMARY KEY (ID_NOTICIA))
CREATE TABLE PROYECTO (ID_PROYECTO BIGINT NOT NULL, DTYPE VARCHAR(31), DESCRIPCION VARCHAR(255), ESTADO VARCHAR(255), FECHA_FINAL DATE, FECHA_INICIO DATE, LUGAR VARCHAR(255), RECONOCIMIENTO VARCHAR(255), ONG_ID BIGINT, PRIMARY KEY (ID_PROYECTO))
CREATE TABLE ACTIVIDADES (ID_PROYECTO BIGINT NOT NULL, CAPACIDAD_ALUMNOS INTEGER, NOMBRE_ACTIVIDAD VARCHAR(255), RECONOCIMIENTOS VARCHAR(255), PRIMARY KEY (ID_PROYECTO))
CREATE TABLE DEMANDA (ID_SOLICITUD BIGINT NOT NULL, COMENTARIOS_ALUMNO VARCHAR(255), COMENTARIOS_ONG VARCHAR(255), COMENTARIOS_PROFESOR VARCHAR(255), ESTADO VARCHAR(255), NOTA FLOAT, ALUMNO_ID BIGINT, PAS_ID BIGINT, PDI_ID BIGINT, PROYECTO_ID_PROYECTO BIGINT, UNIVERSIDAD_ID_UNIVERSIDAD BIGINT, PRIMARY KEY (ID_SOLICITUD))
CREATE TABLE usuario (ID BIGINT NOT NULL, DTYPE VARCHAR(31), CONTRASENA VARCHAR(255), CORREO VARCHAR(255), ROL VARCHAR(255), USUARIO VARCHAR(255), UNIVERSIDAD_ID_UNIVERSIDAD BIGINT, PRIMARY KEY (ID))
CREATE TABLE ALUMNO (ID BIGINT NOT NULL, APELLIDO VARCHAR(255), DNI VARCHAR(255), DIRECCION VARCHAR(255), NOMBRE VARCHAR(255), NUMERO_EXPEDIENTE INTEGER, PRIMARY KEY (ID))
CREATE TABLE ONG (ID BIGINT NOT NULL, DESCRIPCION VARCHAR(255), DIRECCION_WEB VARCHAR(255), NOMBRE VARCHAR(255), REPRESENTANTE VARCHAR(255), TELEFONO INTEGER, CITY VARCHAR(255), COUNTRY VARCHAR(255), STATE VARCHAR(255), STREET1 VARCHAR(255), STREET2 VARCHAR(255), ZIPCODE BIGINT, PRIMARY KEY (ID))
CREATE TABLE PAS (ID BIGINT NOT NULL, FACULTAD VARCHAR(255), REPRESENTANTE VARCHAR(255), SERVICIO VARCHAR(255), TELEFONO VARCHAR(255), CITY VARCHAR(255), COUNTRY VARCHAR(255), STATE VARCHAR(255), STREET1 VARCHAR(255), STREET2 VARCHAR(255), ZIPCODE BIGINT, PRIMARY KEY (ID))
CREATE TABLE PDI (ID BIGINT NOT NULL, APELLIDOS VARCHAR(255), DNI VARCHAR(255), DEPARTAMENTO VARCHAR(255), ES_GESTOR VARCHAR(255), NOMBRE VARCHAR(255), TELEFONO VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE UNIVERSIDAD (ID_UNIVERSIDAD BIGINT NOT NULL, DTYPE VARCHAR(31), DIRECCION_WEB VARCHAR(255), NOMBRE VARCHAR(255), CITY VARCHAR(255), COUNTRY VARCHAR(255), STATE VARCHAR(255), STREET1 VARCHAR(255), STREET2 VARCHAR(255), ZIPCODE BIGINT, PRIMARY KEY (ID_UNIVERSIDAD))
CREATE TABLE VOLUNTARIADO (ID_PROYECTO BIGINT NOT NULL, NOMBRE_VOLUNTARIADO VARCHAR(255), PRIMARY KEY (ID_PROYECTO))
CREATE TABLE Alumno_DESTINOS_DESEADOS (Alumno_ID BIGINT, DESTINOS_DESEADOS VARCHAR(255))
CREATE TABLE Alumno_IDIOMAS (Alumno_ID BIGINT, IDIOMAS VARCHAR(255))
CREATE TABLE Alumno_SOLICITUDES (Alumno_ID BIGINT, SOLICITUDES VARCHAR(255))
CREATE TABLE ONG_NOTICIAS (ONG_ID BIGINT, NOTICIAS VARCHAR(255))
CREATE TABLE ONG_PROYECTO (ONG_ID BIGINT, PROYECTO VARCHAR(255))
CREATE TABLE PAS_SOLICITUDES (PAS_ID BIGINT, SOLICITUDES VARCHAR(255))
CREATE TABLE PDI_SOLICITUDES (PDI_ID BIGINT, SOLICITUDES VARCHAR(255))
CREATE TABLE Proyecto_PARTICIPANTES (Proyecto_ID_PROYECTO BIGINT, PARTICIPANTES VARCHAR(255))
CREATE TABLE Universidad_FACULTADES (Universidad_ID_UNIVERSIDAD BIGINT, FACULTADES VARCHAR(255))
CREATE TABLE Universidad_SOLICITUDES (Universidad_ID_UNIVERSIDAD BIGINT, SOLICITUDES VARCHAR(255))
CREATE TABLE Universidad_USUARIOS (Universidad_ID_UNIVERSIDAD BIGINT, USUARIOS VARCHAR(255))
